engine.script:
$(".tabPanel").tabPanel();
$('.demo form').form();
$('.demo input[name="tags"]').autocomplete({
	source: [
		"ActionScript",
		"AppleScript",
		"Asp",
		"BASIC",
		"C",
		"C++",
		"Clojure",
		"COBOL",
		"ColdFusion",
