		"Fortran",
		"Groovy",
		"Haskell",
		"Java",
		"JavaScript",
		"Lisp",
		"Perl",
		"PHP",
		"Python",
		"Ruby",
		"Scala",
		"Scheme"
	]
});
var keep = function() {
	var tags = $('.demo input[name="tags"]');
	if(!tags.length) {
		return;
	}
	$('.demo input[name="value"]').val(tags.val());
	setTimeout(keep, 500);
}
keep();
