engine.script:

$.ajax({
	url: 'index.html',
	dataType: 'text',
	success: function(text) {
		text = text.replace(/\.\.\/.\.\//g, location.protocol + '//' + location.host + '/');
		$('#content .snippet').text(text);
		$('#content .snippet').snippet('html', {style:"ide-eclipse",showNum:false});
	}
});
